import { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Switch, Link, Route } from 'react-router-dom'
import * as Ethereum from './services/Ethereum'
import styles from './App.module.css'
import MediumEditor from 'medium-editor'
import 'medium-editor/dist/css/medium-editor.css'
import 'medium-editor/dist/css/themes/default.css'

const NewArticle = () => {
  const [editor, setEditor] = useState(null)
  //const contract = useSelector(({ contract }) => contract)

  const dispatch = useDispatch()
  const addArticle = () => dispatch(Ethereum.addArticle)

  useEffect(() => {
    setEditor(new MediumEditor(`.${styles.editable}`, { placeholder : { text : 'Type your article'} }))
  }, [setEditor])

  const handleSubmit = event => {
	alert("Content to send : " + editor.getContent());
	//alert("Contract : " + contract);
	//alert("Result : " + contract.methods.addArticle(2, "test2")).send();
	addArticle()
  }

  return (
    <form onSubmit={handleSubmit}>
      <div className={styles.subTitle}>New article</div>
      <div className={styles.mediumWrapper}>
        <textarea className={styles.editable} />
      </div>
      <input type="submit" value="Submit" />
    </form>
  )
}

const Home = () => {
  return (
    <div className={styles.links}>
      <Link to="/">Home</Link>
      <Link to="/article/new">Add an article</Link>
      <Link to="/article/all">All articles</Link>
    </div>
  )
}


const ReadArticle = ({ id }) => {
  const [content, setContent] = useState([])
  const contract = useSelector(({ contract }) => contract)
  useEffect(() => {
    if (contract) {
      contract.methods.articleContent(id).call().then(setContent)

    }
  }, [contract, setContent])
  return content
}


const AllArticles = () => {
  const [articles, setArticles] = useState([])
  const contract = useSelector(({ contract }) => contract)
  useEffect(() => {
    if (contract) {
      //contract.methods.articleContent(0).call().then(console.log)
      contract.methods.getAllIds().call().then(setArticles)

    }
  }, [contract, setArticles])
  return <div>{articles.map(article => <Link to={`/article/${article}`}> Article {article} </Link> )} </div>
  /*return
  	<div>
		<Route exact="/article/article">
		  <ReadArticle id = {0} />
		</Route>

  	</div>*/
}

const NotFound = () => {
  return <div>Not found</div>
}

const App = () => {
  const dispatch = useDispatch()
  useEffect(() => {
    dispatch(Ethereum.connect)
  }, [dispatch])
  return (
    <div className={styles.app}>
      <div className={styles.title}>Welcome to Decentralized Wikipedia</div>
      <Switch>
        <Route path="/article/new">
          <NewArticle />
        </Route>
        <Route exact path="/">
          <Home />
        </Route>
        <Route path="/article/all">
          <AllArticles />
        </Route>
        //<Route path="/article/:id" component={props => <ReadArticle id={props.match.params.id}/> } />
        <Route path="/article/:id" component={ReadArticle}/>
        <Route>
          <NotFound />
        </Route>
      </Switch>
    </div>
  )
}

export default App
